/**
 * 266. 回文排列
 * https://leetcode.cn/problems/palindrome-permutation/
 */


/**
 * 60ms  47.06%
 * 40.9MB  58.82%
 */
var canPermutePalindrome = function (s) {
    let map = {}

    for (let i = 0; i < s.length; i++) {
        if (!map[s[i]]) {
            map[s[i]] = 1
        } else {
            map[s[i]]++
        }
    }

    let single = null

    for (let key in map) {
        if ((map[key] == 1 || map[key] % 2 == 1) && single) {
            return false
        }

        if (map[key] == 1 || map[key] % 2 == 1) {
            single = map[key]
        } else if (map[key] % 2 != 0 && map[key] % 2 != 1) {
            return false
        }
    }

    return true
};


s = 'code'
s = 'aab'
s = 'aaa'
s = ''



let result = canPermutePalindrome(s)

console.log(result)